mapper.insert:
INSERT INTO M_ASSET(
buildingCode
, assetType
, assetName
, price
, status
, dateFrom
, dateTo
, created
, lastUpdate
, createId
, updateId
)VALUES(
#{dto.buildingCode}
, #{dto.assetType}
, #{dto.assetName}
, #{dto.price}
, #{dto.status}
, #{dto.dateFrom}
, #{dto.dateTo}
,  NOW()
,  NOW()
, #{dto.createId}
, #{dto.updateId}
)

mapper.update:
UPDATE M_ASSET SET
buildingCode = #{dto.buildingCode}
, assetType = #{dto.assetType}
, assetName = #{dto.assetName}
, price = #{dto.price}
, status = #{dto.status}
, dateFrom = #{dto.dateFrom}
, dateTo = #{dto.dateTo}
, updateId = #{dto.updateId}
, lastUpdate= NOW()
WHERE assetCode= #{dto.assetCode}
