mapper.insert:
INSERT INTO M_NEWS(
newsType
, newsHeader
, newsShorter
, created
, lastUpdate
, createId
, updateId
)VALUES(
#{dto.newsType}
, #{dto.newsHeader}
, #{dto.newsShorter}
,  NOW()
,  NOW()
, #{dto.createId}
, #{dto.updateId}
)

INSERT INTO T_NEWS_DETAIL(
newsType
, newsCode
, refNewsCode
, created
, lastUpdate
, createId
, updateId
)VALUES(
#{dto.newsCode}
, #{dto.refNewsCode}
,  NOW()
,  NOW()
, #{dto.createId}
, #{dto.updateId}
)

mapper.update:
UPDATE M_NEWS SET
newsType = #{dto.newsType}
,newsHeader = #{dto.newsHeader}
,newsShorter = #{dto.newsShorter}
,refNewsCode = #{dto.refNewsCode}
,updateId = #{dto.updateId}
,lastUpdate= NOW()
WHERE newsCode= #{dto.newsCode}
