mapper.insert:
        INSERT INTO M_SERVICE (
        serviceType
        , buildingCode
        , serviceName
        , servicePrice
        , serviceStart
        , serviceEnd
        , created
		, lastUpdate
		, createId
		, updateId
        ) VALUE (
        #{dto.serviceType}
        ,#{dto.buildingCode}
        , #{dto.serviceName}
        , #{dto.servicePrice}
        , #{dto.serviceStart}
        , #{dto.serviceEnd}
        ,  NOW()
		,  NOW()
		, #{dto.createId}
		, #{dto.updateId}
        )

mapper.update:
        UPDATE M_SERVICE SET
        serviceType = #{dto.serviceType}
        ,buildingCode = #{dto.buildingCode}
        , serviceName = #{dto.serviceName}
        , servicePrice = #{dto.servicePrice}
        , serviceStart = #{dto.serviceStart}
        , serviceEnd = #{dto.serviceEnd}
        , updateId = #{dto.updateId}
		, lastUpdate= NOW()
		WHERE serviceCode = #{dto.serviceCode}
